import { useEffect, useState } from "react"
import { useRouter } from "next/router"
import axios from "axios"

const Book = () => {
	const { query } = useRouter()

	const [book, setBook] = useState([])

	const getBook = () => {
		return axios.get(
			`https://booksontap.azurewebsites.net/api/Books/${query.slug}`,
		)
	}

	console.log(query)

	useEffect(() => {
		getBook().then(res => {
			setBook(res.data)
		})
	}, [])

	return (
		<>
			<h1>{query.slug}</h1>
			<p>{query.title}</p>
		</>
	)
}

export default Book
